/*
    #****************************************************************************
#                          CntOddEvenNbrs.PY
#
# Generate an arbitrary number of random numbers between a minVal and MaxVal.
#
# Count the number of even and odd numbers generated. Also determine the
# largest and smallest generated odd and even numbers. 
#
# You program should generate the following report....
#
# Even numbers generated: 
# Largest even number: 
# Smallest even number:
#
# Odd numbers generated: 
# Largest odd number: 
# Smallest odd number:
#
#****************************************************************************

*/
    
    public void python2(int minVal, int MaxVal) 
    {
        int even = 0;
        int odd = 0;
        int scale = MaxVal - minVal + 1;
        int[] randn = new int[100];
        for (int i = 0; i < 100; i++)
        {
            randn[i] = ((int)(Math.random() * scale)) + minVal;
        }
        int emax = minVal - 1;
        int emin = MaxVal + 1; 
        int omax = minVal - 1; 
        int omin = MaxVal + 1; 
        for (int i =0; i < 100; i++)
        {
            if (randn[i] % 2 == 0) 
            {
                even++;
                if (randn[i] > emax) 
                {
                    emax = randn[i];
                }
                if (randn[i] < emin) 
                {
                    emin = randn[i];
                }
            }
            if (randn[i] % 2 != 0) 
            {
                odd++;
                if (randn[i] > omax) 
                {
                    omax = randn[i];
                }
                if (randn[i] < omin) 
                {
                    omin = randn[i];
                }
            }
        }
        System.out.println("Even numbers generated: " + even);
        System.out.println("Largest even number: " + emax);
        System.out.println("Smallest even number: " + emin);
        System.out.println();
        System.out.println("Odd numbers generated: " + odd);
        System.out.println("Largest odd number: " + omax);
        System.out.println("Smallest odd number: " + omin);
    }
    
