    public void python3() 
    {
        Scanner input = new Scanner(System.in);
        System.out.println("FACTOR GENERATOR");
        System.out.println();
        System.out.print("Please enter a number --> ");
        System.out.println();
        int numbn = input.nextInt();
        input.close();
        String output = "The factors of " + numbn + " are ";
        for (int i = 1; i < numbn; i++)
        {
            if (numbn % i == 0) 
            {
                output = output + i + ", ";
            }
        }
        output = output + numbn + ".";
        System.out.println(output);
    }
/*
#****************************************************************************    
#                        GuessMyPhrase.PY
# 
# Prompt a user to play a phrase guess game. The computer will allow you
# an arbitrary number of guesses. A sample session for the word "bagel"
# would proceed as follows.
#
#  Welcome to Guess My Phrase
#  Would you like to Play? enter yes \ no -->
#
#  You selected yes. I will give you 8 guesses.
#  My phrase contains 1 word 5 letters.
#  You can enter a word or a letter for each guess.
#
#  Guess #1 --> b
#  b is correct
#  correct guesses: b
#  incorrect guesses: 
#
#  Guess #2 --> w
#  w is not correct
#  correct guesses: b
#  incorrect guesses: w
#
#  Guess #3 --> t
#  t is not correct
#  correct guesse b 
#  incorrect guesses: w, t
#
#  Guess #4 --> a
#  a is correct
#  correct guesses: b, a 
#  incorrect guesses: w, t
#  
#  Guess #5 --> e
#  e is correct
#  correct guesses: b, a, e 
#  incorrect guesses: w, t
#
#
# The game should be ended with one of the following phrases. 
#
# Congratulations! You have guessed all the letters in my phrase.
# Congratulations! You have guessed my phrase!
# Sorry. Game Over. You have used all your Guesses.
# Thank You. Play Again Soon!
#****************************************************************************
*/
    
    public void python4() 
    {
        System.out.println("\nWeclome to Guess My Phrase! \nWould you like to play (yes / no)? --> ");
        Scanner yn = new Scanner(System.in);
        String dec = yn.next();
        char[] cguess= new char[26];
        String right = "";
        String wrong = "";
        boolean wguess = false;
        char[] iguess= new char[26];
        String word = "bagel";
        int guesses = 10;
        boolean repeat = false;
        if (dec.equals("yes")) 
        {
            System.out.println("\nYou selected yes. I will give you "+guesses+" guesses.\nMy phrase contains 1 word and "+word.length()+" letters.\nYou can enter a word or a letter for each guess.\n");
            boolean won = false;
            int g = 0;
            while (won == false)
            {
                g++;
                boolean subtrue = false;
                repeat = false;
                boolean correct = false;
                System.out.println("Guess "+(g)+" --> ");
                String guess = yn.next();
                int place = -1;
                if (guess.length() == 1) 
                {
                    for (int i = 0; i < (word.length()); i++) 
                    {
                        if (word.substring(i, i+1).equals(guess))
                        {
                            subtrue = true;
                            System.out.println("\n"+guess+" is correct");
                            for (int h = 0; h < cguess.length; h++) 
                            {
                                if (cguess[h] == '\0')
                                {
                                    place = h;
                                    break;
                                }
                                else {}
                            }
                            for (int h = 0; h < cguess.length; h++) 
                            {
                                if (cguess[h] == guess.charAt(0))
                                {
                                    repeat = true;
                                    g--;
                                }
                                else {}
                            }
                            if (repeat != true) 
                            {
                                cguess[place] = guess.charAt(0);
                                if (right.length() == 0)
                                {
                                    right = right + guess;
                                }
                                else
                                {
                                    right = right + ", " + guess;
                                }
                            }
                        }
                    }
                    if (subtrue != true) 
                    {
                        System.out.println("\n"+guess+" is incorrect");
                        for (int h = 0; h < iguess.length; h++) 
                            {
                                if (cguess[h] == '\0')
                                {
                                    place = h;
                                    break;
                                }
                                else {}
                            }
                            for (int h = 0; h < cguess.length; h++) 
                            {
                                if (cguess[h] == guess.charAt(0))
                                {
                                    repeat = true;
                                    g--;
                                }
                                else {}
                            }
                            if (repeat != true) 
                            {
                                iguess[place] = guess.charAt(0);
                                if (wrong.length() == 0)
                                {
                                    wrong = wrong + guess;
                                }
                                else
                                {
                                    wrong = wrong + ", " + guess;
                                }
                            }
                    } 
                    System.out.println("Correct guesses: " + right);
                    System.out.println("Incorrect guesses: " + wrong + "\n");
                }
                else 
                {
                    if(guess.equals(word))
                    {
                        won = true;
                        System.out.println();
                    }
                    else
                    {
                        g = guesses;
                        wguess = true;
                    }
                }
                if (cguess[word.length() - 1] != '\0')
                {
                    won = true;
                }
                if (g >= guesses)
                {
                    break;
                }

            }
            if (won == true) 
            {
                System.out.println("Congratulations! You won! The word was: " + word);
                System.out.println("Thank You. Play Again Soon!\n");
            }
            if (won == false && wguess == false) 
            {
                System.out.println("Sorry. Game Over. You have used all your guesses.");
                System.out.println("Thank You. Play Again Soon!\n");
            }
            if (won == false && wguess == true) 
            {
                System.out.println("\nSorry. Game Over. You have guessed incorrectly.");
                System.out.println("Thank You. Play Again Soon!\n");
            }
        }
        else
        {
            System.out.println("See you later!");
        }
        yn.close();
    }
